import { createContext, useState, useEffect } from "react";

export const UserContext = createContext();

function UserProvider({ children }) {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState("");
  const [error, setError] = useState("");

  const fetchUsers = async () => {
    try {
      const response = await fetch(
        "http://localhost:8080/uu-listapp-maing01/22222222222222222222222222222222/user/list"
      );
      if (!response.ok) {
        throw new Error("Failed to fetch users.");
      }
      const data = await response.json();
      console.log(data.userLists.itemList);
      setUsers(data.userLists.itemList);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  console.log(users);

  const userLi = users.reduce((acc, user) => {
    acc[user.id] = user.name;
    return acc;
  }, {});

  console.log(userLi);

  const userList = [
    {
      name: "sam",
      id: "123",
    },
  ];

  const [activeList, setActiveList] = useState("");
  const [loggedInUser, setLoggedInUser] = useState("u1");
  const value = {
    userList,
    loggedInUser,
    setLoggedInUser,
    activeList,
    setActiveList,
    fetchUsers,
  };
  return <UserContext.Provider value={value}>{children}</UserContext.Provider>;
}

export default UserProvider;
