import { createContext, useState, useEffect } from "react";

export const UserContext = createContext();

function UserProvider({ children }) {
  const [users, setUsers] = useState();
  const [loading, setLoading] = useState();
  const [error, setError] = useState();

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch(
          "http://localhost:8080/uu-listapp-maing01/22222222222222222222222222222222/user/list"
        );
        if (!response.ok) {
          throw new Error("Failed to fetch users.");
        }
        const data = await response.json();
        setUsers(data.userLists.itemList); // Save the fetched users in state
        console.log(data.userLists.itemList);
      } catch (err) {
        setError(err.message); // Handle any errors
      } finally {
        setLoading(false); // Turn off loading state
      }
    };

    fetchUsers();
  }, []);

  const userMap = users.reduce((acc, user) => {
    acc[user.id] = user.name;
    return acc;
  }, {});

  const [activeList, setActiveList] = useState("");
  const [loggedInUser, setLoggedInUser] = useState("u1");
  const value = {
    userList: [
      { id: "u1", name: "Adam" },
      { id: "u2", name: "Betty" },
      { id: "u3", name: "Courtney" },
      { id: "u4", name: "David" },
    ],
    loggedInUser,
    setLoggedInUser,
    activeList,
    setActiveList,
  };
  return <UserContext.Provider value={value}>{children}</UserContext.Provider>;
}

export default UserProvider;
