import { createContext, useContext, useState, useEffect } from "react";
import { UserContext } from "./UserProvider";
import { useParams } from "react-router-dom";

import {
  getList,
  updateList,
  manageGuests,
  createItem,
  updateItem,
  deleteItem,
} from "./FetchHelper";

export const ListDetailContext = createContext();

function ListDetailProvider({ children }) {
  const { activeList, setActiveList } = useContext(UserContext);
  const [theList, setTheList] = useState("");
  const { id } = useParams();

  useEffect(() => {
    if (!activeList && id) {
      setActiveList(id);
    }
  }, [id, activeList, setActiveList]);

  useEffect(() => {
    const fetchList = async () => {
      if (activeList) {
        try {
          const fetchedList = await getList(activeList);
          setTheList(fetchedList);
        } catch (error) {
          console.error("Error fetching the list:", error);
        }
      }
    };

    fetchList();
  }, [activeList]);

  useEffect(() => {
    fetchUsers();
  }, []);

  const changeName = async (name) => {
    const listId = activeList;
    const updateData = { name };
    try {
      await updateList(listId, updateData);
      const fetchedList = await getList(activeList);
      setTheList(fetchedList);
    } catch (err) {
      console.error("Error updating list:", err);
    }
  };

  const manageUsers = async (userId, action) => {
    const listId = activeList;
    const updateData = { userId, action };
    try {
      await manageGuests(listId, updateData);
      const fetchedList = await getList(activeList);
      setTheList(fetchedList);
    } catch (err) {
      console.error("Error updating list:", err);
    }
  };

  const addItem = async (listId, state) => {
    const updateData = { archived: state };
    try {
      await createItem(listId, updateData);
      getList(listId);
    } catch (err) {
      console.error("Error updating list:", err);
    }
  };

  const changeItem = async (listId, state) => {
    const updateData = { archived: state };
    try {
      await updateItem(listId, updateData);
      getList(listId);
    } catch (err) {
      console.error("Error updating list:", err);
    }
  };

  const removeItem = async (listId, state) => {
    const updateData = { archived: state };
    try {
      await deleteItem(listId, updateData);
      getList(listId);
    } catch (err) {
      console.error("Error updating list:", err);
    }
  };

  if (!id || !theList) {
    return <p>This URL does not exist.</p>;
  }

  const value = {
    theList,
    changeName,
    addItem,
    manageUsers,
    changeItem,
    removeItem,
  };

  return (
    <ListDetailContext.Provider value={value}>
      {children}
    </ListDetailContext.Provider>
  );
}

export default ListDetailProvider;
