import { createContext, useState } from "react";

import {
  createList,
  fetchLists,
  getList,
  updateList,
  deleteList,
} from "./FetchHelper";

export const ListOverviewContext = createContext();

function ListOverviewProvider({ children }) {
  const [lists, setLists] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const addList = async (newList) => {
    try {
      const createdList = await createList(newList);
      setLists((prevLists) => [...prevLists, createdList]);
    } catch (err) {
      console.error("Error creating list:", err);
    }
  };

  const loadLists = async () => {
    setLoading(true);
    setError("");
    try {
      const data = await fetchLists();
      setLists(data);
    } catch (err) {
      setError("Failed to load lists");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const loadList = async () => {
    setLoading(true);
    setError("");
    try {
      const data = await getList();
      setLists(data);
    } catch (err) {
      setError("Failed to load lists");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const removeList = async (id) => {
    try {
      await deleteList(id);
      setLists((prevLists) => prevLists.filter((list) => list.id !== id));
    } catch (err) {
      console.error("Error deleting list:", err);
    }
  };

  const changeList = async (id, updatedData) => {
    try {
      const updatedList = await updateList(id, updatedData);
      setLists((prevLists) =>
        prevLists.map((list) => (list.id === id ? updatedList : list))
      );
    } catch (err) {
      console.error("Error updating list:", err);
    }
  };

  const value = {
    addList,
    loadLists,
    loadList,
    removeList,
    changeList,
  };

  return (
    <ListOverviewContext.Provider value={value}>
      {children}
    </ListOverviewContext.Provider>
  );
}

export default ListOverviewProvider;
